import { useEffect } from 'react';

export default function useExternalScripts({ url }){
  useEffect(() => {
    const script = document.createElement("script");

    script.src = url;
    script.async = true
    document.body.appendChild(script);

    return () => {
      script.removeChild(script);
    };
  }, [url]);

};
